import { Layout, Menu } from "antd";
import { useEffect } from "react";
import { UserOutlined } from "@ant-design/icons";
import Page from "./Page";
import Login from "./Login";


const Navbar = (props) => {

  const {setActiveComponent} = props;
  useEffect(() => {
    setActiveComponent(<Page title="item" />);
  },[setActiveComponent])
  // on a une liste d'objet qui s'appelle Label, on abesoin de recuperer pour chaque label, la clé et le titre pour pouvoir generer le contenu :
  // <Menu>
  //    <Menu.Item><Menu.Item/>
  //    <Menu.Item><Menu.Item/>
  // Je parcours labels

  const labels = [
    {
      key: "I1",
      action: () => setActiveComponent(<Page title="item1" />),
      title: "Item 1",
    },
    {
      key: "I2",
      action: () => setActiveComponent(<Page title="item2" />),
      title: "Item 2",
    },
    {
      key: "connexion",
      action: () => setActiveComponent(<Login />),
      title: "Connexion",
      icon: <UserOutlined />,
    },
  ];

  const createMenuItem = (label) => {
    return (
      <Menu.Item key={label.key} onClick={label.action}>
        {label.icon}
        {label.title}
      </Menu.Item>
    );
  }

  return (
    <Layout.Header>
      <Menu mode="horizontal" defaultSelectedKeys={["I1"]}>
        {labels.map((label) => createMenuItem(label))}
        {/* <Menu.Item
          key="I1"
          value="I1"
          onClick={(event) => console.log("item 1", event.key)}
        >
          Item 1
        </Menu.Item>
        <Menu.Item
          key="I2"
          value="I2"
          onClick={(event) => console.log("item 2", event.key)}
        >
          Item 2
        </Menu.Item>
        <Menu.Item
          key="connexion"
          onClick={(event) => console.log("item 2", event.key)}
        >
          <UserOutlined /> Connexion
        </Menu.Item> */}
      </Menu>
    </Layout.Header>
  );
};

export default Navbar;
