        },
        {
            key: "connexion",
            action: () => setActiveComponent( < Login / > ),
            title: "Connexion",
            icon: < UserOutlined / >
        },
    ];

    const createMenuItem = (label) => {
        return ( <
            Menu.item key = { label.key }
            onClick = { label.action } > { label.icon } { label.title } <
            /Menu.item>
        );
    }

    return ( <
        Layout.Header >
        <
        Menu mode = "horizontal"
        defaultSelectedKeys = {
            ["I1"]
        } > { labels.map((label) => createMenuItem(label)) } <
        /Menu> < /
        Layout.Header >

    )
};
export default Navbar;
