const{setActiveComponent} = props;
useEffect(() => {
  console.log("UE");
  props.setActiveComponent(<Page title="Item 1" />);
}, [setActiveComponent])



  const labels = [
    {
        key : "I1",
        action : () => setActiveComponent(<Page title="Item 1" />),
        title : "Item 1",
     //   component : <Page title="Item 1"/>
    },

    {
        key : "I2",
        action : () => setActiveComponent(<Page title="Item 2" />),
        title : "Item 2",
     //   component : <Page title="Item 2"/>
    },
    {
        key : "connexion",
        action : () => setActiveComponent(<Login />),
        title : "Connexion",
        icon : <UserOutlined />
    },
  ];

  const createMenuItem = (label) => {
      return(
          <Menu.item key = {label.key} onClick={label.action}>
              {label.icon} 
              {label.title}
          </Menu.item>
      );
  }

  return (
    <Layout.Header>
      <Menu mode="horizontal" defaultSelectedKeys={["I1"]}>
        {labels.map((label) => createMenuItem(label))}
        </Menu>
    </Layout.Header>

)
};
export default Navbar;
